export async function addToFav(userId: string, cardToAdd: Card) {
  try {
    let res = await getFav(userId);

    const cardInFav = res.data[0].favCards.some((card: Card) => card._id === cardToAdd._id);
    if (!cardInFav) {
      res.data[0].favCards.push({ ...cardToAdd });
      successMsg("Card added to favorites.");
      return await axios.patch(`${api}/${res.data[0]._id}`, {
        favCards: res.data[0].favCards,
      });
    } else {
      successMsg("Card is already in favorites.");
    }
  } catch (error) {
    console.log(error);
  }
}

export function createFav(_id: string) {
  return axios.post(api, { _id: _id, favCards: [] });
}

export function checkFavCard(favToCheck: Card, _id: string) {
  return axios.get(`${api}/${_id}`);
}

export async function removeFromFav(userId: string, cardId: string) {
  try {
    let res = await getFav(userId);
    res.data[0].favCards = res.data[0].favCards.filter((favCard: Card) => favCard._id !== cardId);
    return axios.patch(`${api}/${res.data[0]._id}`, {
      favCards: res.data[0].favCards,
    });
  } catch (error) {
    console.log(error);
  }
}
