import { FunctionComponent, useEffect, useState } from "react";
import { useNavigate, useParams } from "react-router-dom";
import Card from "../interfaces/Card";
import { getCardById } from "../services/cardsServices";

interface CardInfoProps {}

const CardInfo: FunctionComponent<CardInfoProps> = () => {
  let { id } = useParams();
  let navigate = useNavigate();

  let [card, setCard] = useState<Card>({
    title: "",
    subTitle: "",
    description: "",
    phone: "",
    email: "",
    web: "",
    imageUrl: "",
    imageAlt: "",
    state: "",
    country: "",
    city: "",
    street: "",
    houseNumber: 0,
    zip: "",
  });

  useEffect(() => {
    getCardById(Number(id))
      .then((res) => {
        console.log(res.data);
        setCard(res.data);
      })
      .catch((error) => console.log(error));
  }, []);
  return (
    <>
      {/* <div className="container-fluid">
        <div className="d-flex flex-row card w-50">
          <div className="col-md-6">
            <img
              src={card.imageUrl}
              className="card-img-left "
              alt={card.imageAlt}
              //   width={"200px"}
              //   height={"150px"}
            />
          </div>
          <div className="card-body col-md-6">
            <h5 className="card-title">{card.title}</h5>
            <h5 className="card-title">{card.subTitle}</h5>
            <hr />
            <p className="card-text">{card.description}</p>
            <p className="card-text">Phone: {card.phone}</p>
            <p className="card-text">Email: {card.email}</p>
            <p className="card-text">WebSite: {card.web}</p>
            <p className="card-text">country: {card.country}</p>
            <p className="card-text">city: {card.city}</p>
            <p className="card-text">street: {card.street}</p>
            <p className="card-text">zip: {card.zip}</p>
          </div>
        </div>
      </div> */}

      <div
        className="card d-flex flex-md-row"
        style={{ maxWidth: "400px", maxHeight: "800px", margin: "15px", padding: "10px" }}
      >
        {/* img div */}
        <div className="card col " style={{ maxHeight: "300px" }}>
          <img
            className="card-img-top object-fit-cover maxWidth"
            src={card.imageUrl}
            alt="Card image"
            style={{ maxWidth: "100vw", maxHeight: "auto", objectFit: "contain" }}
          />
        </div>
        <div className="card-body col">
          <h5 className="card-title">{card.title}</h5>
          <h5 className="card-title">{card.subTitle}</h5>
          <hr />
          <p className="card-text">{card.description}</p>
          <p className="card-text">Phone: {card.phone}</p>
          <p className="card-text">Email: {card.email}</p>
          <p className="card-text">WebSite: {card.web}</p>
          <p className="card-text">country: {card.country}</p>
          <p className="card-text">city: {card.city}</p>
          <p className="card-text">street: {card.street}</p>
          <p className="card-text">zip: {card.zip}</p>

          <a href="#" className="btn btn-primary">
            See Profile
          </a>
        </div>
      </div>
    </>
  );
};

export default CardInfo;
