import axios from "axios";
import Card from "../interfaces/Card";
import Favorite from "../interfaces/Favorite";

let api: string = `${process.env.REACT_APP_API}/favorites`;

// export function getFavByCurrentUser(id: number) {
//   return axios.get<Favorite[]>(`${api}?id=${id}`);
// }
// export function deleteFavById(id: number) {
//   return axios.delete(`${api}/${id}`);
// }

export function getFav(userId: number) {
  return axios.get(`${api}?userId=${userId}`);
}

export function createFav(userId: number) {
  return axios.post(api, { userId, cards: [] });
}
export async function addToFav(userId: number, cardToAdd: Card) {
  try {
    let res = await getFav(userId);
    if (res.data.length === 0) {
      await createFav(userId);
      res = await getFav(userId);
    }
    // checkIfInFav(userId, id);
    res.data[0].cards.push(cardToAdd);
    return axios.patch(`${api}/${res.data[0].id}`, {
      cards: res.data[0].cards,
    });
  } catch (error) {
    console.log(error);
  }
} // add to cart / update cart
export async function addToCart(userId: number, productToAdd: Product) {
  try {
    // 1. search for the exising cart products
    let res = await getCart(userId);
    // 2. add the new product to the products array
    res.data[0].products.push({ ...productToAdd, quantity: 1 });
    // 3. update the cart - put or patch
    return axios.patch(`${api}/${res.data[0].id}`, {
      products: res.data[0].products,
    });
  } catch (error) {
    console.log(error);
  }
}

export async function removeFromFav(userId: number, cardId: number) {
  try {
    let res = await getFav(userId);
    res.data[0].cards = res.data[0].cards.filter((card: Card) => card.id !== cardId);
    return axios.patch(`${api}/${res.data[0].id}`, {
      cards: res.data[0].cards,
    });
  } catch (error) {
    console.log(error);
  }
}

export function checkIfInFav(userId: number, cardIds: number[]) {
  return axios.get(`${api}/${userId}/cards`, {
    params: {
      cardIds: cardIds.join(","),
    },
  });
}
