import { FunctionComponent, useEffect, useState } from "react";
import Card from "../interfaces/Card";
import { getFav, removeFromFav } from "../services/favServices";
import Favorite from "../interfaces/Favorite";
import { Link, useParams } from "react-router-dom";
import { successMsg } from "../services/feedbacksServices";

interface FavCardsProps {
  userInfo: any;
}

const FavCards: FunctionComponent<FavCardsProps> = ({ userInfo }) => {
  let [favCards, setFavCards] = useState<Card[]>([]);
  let [favCardsChanged, setFavCardsChanged] = useState<boolean>(false);

  useEffect(() => {
    getFav(userInfo.userId)
      .then((res) => {
        setFavCards(res.data[0].favCards);
        console.log(res.data);
      })
      .catch((err) => console.log(err));
  }, [favCardsChanged]);

  const handleDeleteFromFav = (cardId: number) => {
    if (window.confirm("Are you sure you want to remove this card from favorites?")) {
      removeFromFav(userInfo.userId, cardId)
        .then((res) => {
          setFavCardsChanged(!favCardsChanged);
          successMsg("Card removed from favorites!");
        })
        .catch((err) => console.log(err));
    }
  };
  return (
    <>
      <div className="container">
        {favCards.length ? (
          <div className="row row-cols-1 row-cols-md-3 g-4 justify-content-center ">
            {cards.map((card: Card) => (
              <div
                key={card.id}
                className="card mb-3 mx-2"
                style={{ maxWidth: "340px", maxHeight: "500px" }}
              >
                <div className=" g-0 mx-auto align-content-between">
                  <div className="text-center">
                    <img
                      src={card.imageUrl}
                      className="rounded-start p-1 object-fit-contain "
                      alt={card.imageAlt}
                      width={"200px"}
                      height={"150px"}
                    />
                  </div>
                  <div className="col-md-12">
                    <div className="card-body d-flex flex-column">
                      <h5 className="card-title">{card.title}</h5>
                      <p className="card-text">{card.description}</p>
                      <hr />
                      <p className="card-text">Phone: {card.phone}</p>
                      <p className="card-text">Email: {card.email}</p>
                      <p className="card-text">City: {card.city}</p>
                      <div className="container d-flex justify-content-around">
                        {userInfo.role === "admin" && (
                          <>
                            <Link to="/" onClick={() => handleDelete(card)}>
                              <i className="bi bi-trash3-fill" style={{ fontSize: "22px" }}></i>
                            </Link>
                            <Link to={`/cards/update/${card.id}`}>
                              <i
                                className="bi bi-pencil-square p-auto"
                                style={{ fontSize: "22px" }}
                              ></i>
                            </Link>
                          </>
                        )}
                        {userInfo.role && (
                          <Link to="" onClick={() => handleDeleteFromFav(card.id as number)}>
                            <i
                              className="bi bi-heart-fill"
                              style={{ color: "#df0c0c", fontSize: "22px" }}
                            />
                          </Link>
                        )}
                        <i
                          className="bi bi-info-circle"
                          style={{ fontSize: "22px" }}
                          onClick={() => navigate(`/cards/${card.id}`)}
                        ></i>
                      </div>
                    </div>
                  </div>
                </div>
              </div>
            ))}
          </div>
        ) : (
          <h3>No cards to display</h3>
        )}
      </div>
    </>
  );
};
export default FavCards;
