import axios from "axios";
import User from "../interfaces/User";

let api: string = `${process.env.REACT_APP_API}/users`;

export function checkUser(userToCheck: Pick<User, "email" | "password">) {
  return axios.post(`${api}?email=${userToCheck.email}&password=${userToCheck.password}`);
}

export function addUser(newUser: User) {
  return axios.post(api, newUser);
}

export function updateUser(updatedUser: User, _id: string) {
  return axios.put(`${api}/${_id}`, updatedUser);
}

export function getUserById(_id: string) {
  return axios.get(`${api}/${_id}`);
}

export function getUsers() {
  return axios.get(api);
}

export function deleteUser(_id: string) {
  return axios.delete(`${api}/${_id}`);
}
