import axios from "axios";
import Card from "../interfaces/Card";
import Favorite from "../interfaces/Favorite";
import { successMsg } from "./feedbacksServices";

let api: string = `${process.env.REACT_APP_API}/favorites`;

export function getFav(id: number) {
  return axios.get(`${api}?id=${id}`);
}

// add to fav / update fav
export async function addToFav(userId: number, cardToAdd: Card) {
  try {
    let res = await getFav(userId);
    if (res.data.length === 0) {
      await createFav(userId);
      res = await getFav(userId);
    }

    const cardInFav = res.data[0].favCards.some((card: Card) => card.id === cardToAdd.id);
    if (!cardInFav) {
      res.data[0].favCards.push({ ...cardToAdd });

      successMsg("Card added to favorites.");
      return await axios.patch(`${api}/${res.data[0].id}`, {
        favCards: res.data[0].favCards,
      });
    } else {
      successMsg("Card is already in favorites.");
    }
  } catch (error) {
    console.log(error);
  }
}

export function createFav(id: number) {
  return axios.post(api, { id: id, favCards: [] });
}

export function checkFavCard(favToCheck: Card, id: number) {
  return axios.get(`${api}/${id}`);
}

export async function removeFromFav(userId: number, cardId: number) {
  try {
    let res = await getFav(userId);
    res.data[0].favCards = res.data[0].favCards.filter((favCard: Card) => favCard.id !== cardId);
    return axios.patch(`${api}/${res.data[0].id}`, {
      favCards: res.data[0].favCards,
    });
  } catch (error) {
    console.log(error);
  }
}
