import { useState } from "react";
import "./App.css";
import Home from "./components/Home";
import Navbar from "./components/Navbar";
import Register from "./components/Register";
import { BrowserRouter as Router, Routes, Route } from "react-router-dom";
import Login from "./components/Login";
import CreateCard from "./components/CreateCard";
import UserCards from "./components/UserCards";
import FavCards from "./components/FavCards";
import Footer from "./components/Footer";
import UpdateCard from "./components/UpdateCard";
import Card from "./interfaces/Card";

function App() {
  let [userInfo, setUserInfo] = useState(
    JSON.parse(sessionStorage.getItem("userInfo") as string) == null
      ? { email: false, isAdmin: false }
      : JSON.parse(sessionStorage.getItem("userInfo") as string)
  );

  let [favCards, setFavCards] = useState<Card[]>([]);
  let isCardFavorite = (card: Card) => favCards.some((favCard) => favCard.id === card.id);

  const handleFavorite = (card: Card) => {
    if (!favCards.find((favorite) => favorite.id === card.id)) {
      setFavCards([...favCards, card]);
    }
  };

  return (
    <div className="App">
      <Router>
        <Navbar setUserInfo={setUserInfo} userInfo={userInfo} />
        <Routes>
          <Route
            path="/home"
            element={
              <Home
                userInfo={userInfo}
                handleFavorite={handleFavorite}
                favCards={favCards}
                isCardFavorite={isCardFavorite}
              />
            }
          />
          <Route
            path="/fav-cards"
            element={
              <FavCards
                favCards={favCards}
                setFavCards={setFavCards}
                isCardFavorite={isCardFavorite}
                handleFavorite={handleFavorite}
              />
            }
          />
          <Route path="/login" element={<Login setUserInfo={setUserInfo} userInfo={userInfo} />} />
          <Route path="/register" element={<Register setUserInfo={setUserInfo} />} />
          <Route path="/cards/new" element={<CreateCard userInfo={userInfo} />} />
          <Route path="/user-cards" element={<UserCards userInfo={userInfo} />} />
          <Route path="/cards/update/:id" element={<UpdateCard />} />
        </Routes>
      </Router>
      <Footer />
    </div>
  );
}

export default App;
