import { FunctionComponent } from "react";
import { deleteCard } from "../services/cardsServices";
import { Link } from "react-router-dom";

interface UserCardsProps {}

const UserCards: FunctionComponent<UserCardsProps> = () => {
  return (
    <>
      <div className="container">
        {cards.length ? (
          <div className="row row-cols-1 row-cols-md-3 g-4">
            {cards.map((card: Card) => (
              <div key={card.id} className="card mb-3 mx-2" style={{ maxWidth: "540px" }}>
                <div className="row g-0">
                  <div className="col-md-4 object-fit-cover">
                    <img
                      src={card.imageUrl}
                      className="img-fluid rounded-start "
                      alt={card.imageAlt}
                    />
                  </div>
                  <div className="col-md-8">
                    <div className="card-body">
                      <h5 className="card-title">{card.title}</h5>
                      <p className="card-text">{card.description}</p>
                      {userInfo.role === "admin" && (
                        <>
                          <Link
                            to="/home"
                            className="btn btn-danger"
                            onClick={() => deleteCard(card.id as number)}
                          >
                            <i className="fa-solid fa-trash"></i>
                          </Link>
                          <Link
                            to="/home"
                            className="btn btn-danger"
                            onClick={() => deleteCard(card.id as number)}
                          >
                            <i className="fa-regular fa-pen-to-square"></i>
                          </Link>
                        </>
                      )}
                      {(userInfo.role === "user" || userInfo.role === "business") && (
                        <>
                          <Link
                            to="/home"
                            className="btn btn-danger"
                            onClick={() => deleteCard(card.id as number)}
                          >
                            <i className="fa-sharp fa-regular fa-heart fa-xs"></i>
                          </Link>
                        </>
                      )}
                    </div>
                  </div>
                </div>
              </div>
            ))}
          </div>
        ) : (
          <h3>It seems like you have not created cards yet</h3>
        )}
      </div>
    </>
  );
};

export default UserCards;
