import { FunctionComponent, useState } from "react";
import Card from "../interfaces/Card";

interface FavCardsProps {}

const FavCards: FunctionComponent<FavCardsProps> = () => {
  const [favCards, setFavCards] = useState<Card[]>([]);

  const handleFavCards = (card: Card) => {
    if (!favCards.find((favCard) => favCard.id === card.id)) {
      setFavCards([...favCards, card]);
    }
  };

  return (
    <div>
      <h1>My Favorite Page</h1>
      {favCards.map((card) => (
        <div key={card.id}>
          <h3>{card.title}</h3>
          {/* Render the rest of the card's content */}
        </div>
      ))}

      {/* Render the cards with a favorite button */}
      {favCards.map((card) => (
        <div key={card.id}>
          <h3>{card.title}</h3>
          {/* Render the rest of the card's content */}
          <button onClick={() => handleFavCards(card)}>Favorite</button>
        </div>
      ))}
    </div>
  );
};

export default FavCards;
