import axios from "axios";
import User from "../interfaces/User";

let api: string = `${process.env.REACT_APP_API}/users`;

export function checkUser(userToCheck: Pick<User, "email" | "password">) {
  return axios.get(`${api}?email=${userToCheck.email}&password=${userToCheck.password}`);
}

export function addUser(newUser: User) {
  return axios.post(api, newUser);
}

export function updateUser(updatedUser: User, userId: number) {
  return axios.put(`${api}/?userId=${userId}`, updatedUser);
}

export function getUserById(userId: number) {
  return axios.get(`${api}?userId=${userId}`);
}
export function getAllUsers() {
  return axios.get(api);
}
export function deleteUser(userId: number) {
  return axios.delete(`${api}/${userId}`);
}
