import axios from "axios";
import Card from "../interfaces/Card";
import Favorite from "../interfaces/Favorite";

let api: string = `${process.env.REACT_APP_API}/favorites`;

export function getFav(id: number) {
  return axios.get(`${api}?id=${id}`);
}
// export function removeFromFav(id: number, id: number) {
//   return axios.delete(`${api}/${id}/?id=${id}`);
// }
// add to cart / update cart
export async function addToFav(id: number, cardToAdd: Card) {
  try {
    // 1. search for the exising cart products
    let res = await getFav(id);
    // 2. add the new product to the products array
    res.data[0].favCards.push({ cardToAdd });
    // 3. update the cart - put or patch
    return axios.patch(`${api}/${res.data[0].id}`, {
      favCards: res.data[0].favCards,
    });
  } catch (error) {
    console.log(error);
  }
}
// create fav
export function createFav(userId: number) {
  return axios.post(api, { userId: userId, favCards: [] });
}
