import { FunctionComponent, useState } from "react";
import Card from "../interfaces/Card";
import { Link } from "react-router-dom";

interface FavCardsProps {
  favCards: Card[];
  setFavCards: Function;
  handleFavorite: Function;
}

const FavCards: FunctionComponent<FavCardsProps> = ({ favCards, setFavCards, handleFavorite }) => {
  return (
    <>
      <div>
        <h2>Favorite Page</h2>
        {favCards.map((card) => (
          <div key={card.id} className="card mb-3 mx-2" style={{ maxWidth: "540px" }}>
            <div className=" g-0">
              <div className="object-fit-cover" style={{ width: "250px", height: "200" }}>
                <img
                  src={card.imageUrl}
                  className="img-fluid rounded-start p-1 center"
                  alt={card.imageAlt}
                  // width={"200px"}
                  // height={"150px"}
                />
              </div>
              <div className="col-md-8">
                <div className="card-body ">
                  <h5 className="card-title">{card.title}</h5>
                  <p className="card-text">{card.description}</p>
                  <hr />
                  <p className="card-text">Phone: {card.phone}</p>
                  <p className="card-text">Email: {card.email}</p>
                  <p className="card-text">City: {card.city}</p>

                  <Link to="/home" onClick={() => handleFavorite(card)}>
                    <i
                      className="fa-regular fa-heart"
                      color={isCardFavorite(card) ? "red" : "gray"}
                    ></i>
                  </Link>
                </div>
              </div>
            </div>
          </div>
        ))}
      </div>
    </>
  );
};

export default FavCards;
