import { useFormik } from "formik";
import { FunctionComponent } from "react";
import * as yup from "yup";
import { createCard } from "../services/cardsServices";

interface CreateCardProps {
  setUserInfo: Function;
}

const CreateCard: FunctionComponent<CreateCardProps> = ({ setUserInfo }) => {
  let formik = useFormik({
    initialValues: {
      title: "",
      subTitle: "",
      description: "",
      phone: "",
      email: "",
      web: "",
      imageUrl: "",
      imageAlt: "",
      state: "",
      country: "",
      city: "",
      street: "",
      houseNumber: 0,
      zip: "",
    },

    validationSchema: yup.object({
      title: yup.string().required().min(2, "Please enter a valid title"),
      subTitle: yup.string().required().min(2),
      description: yup.string().required().min(2, "Please enter a valid name"),
      phone: yup.string().required().min(6, "Please enter a valid Phone number"),
      email: yup.string().required().email(),
      web: yup.string().url(),
      imageUrl: yup.string().required().url(),
      imageAlt: yup.string().required().min(2),
      state: yup.string(),
      country: yup.string().required(),
      city: yup.string().required(),
      street: yup.string().required(),
      houseNumber: yup.number().required(),
      zip: yup.string().required(),
    }),
    onSubmit(values) {
      setUserInfo(JSON.parse(sessionStorage.getItem("userInfo") as string));
      let userId: number = JSON.parse(sessionStorage.getItem("userInfo") as string).userId;
      createCard(userId)
        .then((res) => {
          console.log(values);
        })
        .catch((err) => console.log(err));
    },
  });
  return (
    <>
      <div className="container-fluid col-md-9 p-5">
        {/***** form *******/}
        <form
          className="text-center p-2 border border-info border-5 rounded-3"
          onSubmit={formik.handleSubmit}
        >
          <h3 className="display-3 bg-info">Create card</h3>
          <div className="row">
            <div className="form-floating mb-3 col">
              <input
                type="text"
                className="form-control"
                id="firstName"
                placeholder="Title"
                name="title"
                value={formik.values.title}
                onChange={formik.handleChange}
