import { useFormik } from "formik";
import { FunctionComponent } from "react";
import * as yup from "yup";
import { createCard } from "../services/cardsServices";
import { useNavigate } from "react-router-dom";
import { successMsg } from "../services/feedbacksServices";

interface CreateCardProps {
  userInfo: string;
}

const CreateCard: FunctionComponent<CreateCardProps> = ({ userInfo }) => {
  let navigate = useNavigate();
  let formik = useFormik({
    initialValues: {
      title: "",
      subTitle: "",
      description: "",
      phone: "",
      email: "",
      web: "",
      imageUrl: "",
      imageAlt: "",
      state: "",
      country: "",
      city: "",
      street: "",
      houseNumber: "",
      zip: 0,
    },

    validationSchema: yup.object({
      title: yup.string().min(2, "Please enter a valid title").required(),
      subTitle: yup.string().min(2, "Please enter a valid sub title").required(),
      description: yup.string().min(2, "Please enter a valid description").required(),
      phone: yup.string().min(6, "Please enter a valid Phone number"),
      email: yup.string().email().required(),
      web: yup.string(),
      imageUrl: yup.string().url(),
      imageAlt: yup.string(),
      state: yup.string(),
      country: yup.string(),
      city: yup.string(),
      street: yup.string(),
      houseNumber: yup.string(),
      zip: yup.number(),
    }),
    onSubmit: (values) => {
      let userId: string = JSON.parse(sessionStorage.getItem("userInfo") as string)._id;
      createCard(userId, values)
        .then((res) => {
          navigate(-1);
          successMsg("Card added successfully!");
        })
        .catch((error) => console.log(error));
    },
  });
  return (
    <>
      <div className="container-fluid col-md-9 p-5">
        {/***** form *******/}
        <form
          className="text-center p-2 border border-info border-5 rounded-3"
          onSubmit={formik.handleSubmit}
        >
          <h3 className="display-3 bg-info">Create card</h3>
          <div className="row">
            <div className="form-floating mb-3 col">
              <input
                type="text"
                className="form-control"
                id="firstName"
                placeholder="Title"
                name="title"
                value={formik.values.title}
                onChange={formik.handleChange}
                onBlur={formik.handleBlur}
              />
              <label htmlFor="firstName">Title</label>
              {formik.touched.title && formik.errors.title && (
                <small className="text-danger">{formik.errors.title}</small>
              )}
            </div>
            <div className="form-floating mb-3 col">
              <input
                type="text"
                className="form-control"
                id="subTitle"
                placeholder="subTitle"
                name="subTitle"
                value={formik.values.subTitle}
                onChange={formik.handleChange}
                onBlur={formik.handleBlur}
              />
              <label htmlFor="subTitle">subTitle</label>
