import axios from "axios";
import Card from "../interfaces/Card";
import Favorite from "../interfaces/Favorite";

let api: string = `${process.env.REACT_APP_API}/favorites`;

export function addToFav(currentUser: number, cardToAdd: Card) {
  return axios.post(api, { currentUser, ...cardToAdd });
}
export function getFav(currentUser: number) {
  return axios.get(`${api}?currentUser=${currentUser}`);
}
export function removeFromFav(id: number, currentUser: number) {
  return axios.delete(`${api}/${id}/?currentUser=${currentUser}`);
}
