import { FunctionComponent, useEffect, useState } from "react";
import Card from "../interfaces/Card";
import { deleteCard, getCards } from "../services/cardsServices";
import { Link, useNavigate } from "react-router-dom";
import { addToFav, getFav } from "../services/favServices";
import { successMsg } from "../services/feedbacksService";

interface HomeProps {
  userInfo: any;
}

const Home: FunctionComponent<HomeProps> = ({ userInfo }) => {
  let [cards, setCards] = useState<Card[]>([]);
  let navigate = useNavigate();

  useEffect(() => {
    getCards()
      .then((res) => setCards(res.data))
      .catch((err) => console.log(err));
  }, []);

  let handleAddToFav = (card: Card) => {
    let id = userInfo.userId;
    addToFav(id, card)
      .then((res) => successMsg("Card added successfully"))
      .catch((err) => console.log(err));
  };
  return (
    <>
      <div className="container ">
        {cards.length ? (
          <div className="row row-cols-1 row-cols-md-3 g-4 justify-content-center ">
            {cards.map((card: Card) => (
              <div
                key={card.id}
                className="card mb-3 mx-2"
                style={{ maxWidth: "340px", maxHeight: "500px" }}
              >
                <div className=" g-0 mx-auto">
                  <div
                    className="text-center"
                    // style={{ width: "250px", height: "200" }}
                  >
                    <img
                      src={card.imageUrl}
                      className="rounded-start p-1 object-fit-contain "
                      alt={card.imageAlt}
                      width={"200px"}
                      height={"150px"}
                    />
                  </div>
                  <div className="col-md-12">
                    <div className="card-body">
                      <h5 className="card-title">{card.title}</h5>
                      <p className="card-text">{card.description}</p>
                      <hr />
                      <p className="card-text">Phone: {card.phone}</p>
                      <p className="card-text">Email: {card.email}</p>
                      <p className="card-text">City: {card.city}</p>
                      <div className="d-flex justify-content-between">
                        {userInfo.role === "admin" && (
                          <ul className="ps-0">
                            <Link to="/home" onClick={() => deleteCard(card.id as number)}>
                              <i className="fa-solid fa-trash"></i>
                            </Link>
                            <Link to="/home">
                              <i className="fa-regular fa-pen-to-square"></i>
                            </Link>
                          </ul>
                        )}
                        <ul>
                          {userInfo.role && (
                            <Link to="" onClick={() => handleAddToFav(card)}>
                              <i
                                className="fa-regular fa-heart fa-xl"
                                style={{ color: "#df0c0c" }}
                              />
                            </Link>
                          )}
                          <Link
                            to={`/cards/${card.id}`}
                            // onClick={() => navigate(`/cards/${card.id}`)}
                          >
                            <i className="fa-solid fa-xl fa-circle-info" />
                          </Link>
                        </ul>
                      </div>
                    </div>
                  </div>
                </div>
              </div>
            ))}
          </div>
        ) : (
          <h3>No cards to display</h3>
        )}
      </div>
    </>
  );
};

export default Home;
