import { FunctionComponent, useEffect, useState } from "react";
import { deleteCard, getCardsByUserId } from "../services/cardsServices";
import { Link, useNavigate } from "react-router-dom";
import Card from "../interfaces/Card";
import { render } from "react-dom";

interface UserCardsProps {
  userInfo: any;
}

const UserCards: FunctionComponent<UserCardsProps> = ({ userInfo }) => {
  let navigate = useNavigate();
  let [cards, setCards] = useState<Card[]>([]);
  let [cardsChanged, setCardsChanged] = useState<boolean>(false);

  let render = () => {
    setCardsChanged(!cardsChanged);
  };
  useEffect(() => {
    getCardsByUserId(userInfo.userId)
      .then((res) => setCards(res.data))
      .catch((err) => console.log(err));
  }, []);
  let handleDelete = (id: number) => {
    if (window.confirm("Are you sure?")) {
      deleteCard(id)
        .then((res) => {
          // successMsg("Product deleted successfully!");
          render();
        })
        .catch((err) => console.log(err));
    }
  };

  return (
    <>
      <div className="container">
        {cards.length ? (
          <div className="row row-cols-1 row-cols-md-3 g-4">
            {cards.map((card: Card) => (
              <div key={card.id} className="card mb-3 mx-2" style={{ maxWidth: "540px" }}>
                <div className="row g-0">
                  <div className="col-md-4 object-fit-cover">
                    <img
                      src={card.imageUrl}
                      className="img-fluid rounded-start "
                      alt={card.imageAlt}
                    />
                  </div>
                  <div className="col-md-8">
                    <div className="card-body">
                      <h5 className="card-title">{card.title}</h5>
                      <p className="card-text">{card.description}</p>
                      <>
                        <Link
                          to="/home"
                          className="btn btn-danger"
                          onClick={() => handleDelete(card.id as number)}
                        >
                          <i className="fa-solid fa-trash"></i>
                        </Link>
                        <Link to={`/cards/update/${card.id}`} className="btn btn-danger">
                          <i className="fa-regular fa-pen-to-square"></i>
                        </Link>
                      </>

                      <>
                        <Link to="/home" className="btn btn-danger">
                          <i className="fa-sharp fa-regular fa-heart fa-xs"></i>
                        </Link>
                      </>
                    </div>
                  </div>
                </div>
              </div>
            ))}
          </div>
        ) : (
          <h3>It seems like you have not created cards yet</h3>
        )}
        <button type="button" className="btn btn-primary" onClick={() => navigate("/cards/new")}>
          Add card
        </button>
      </div>
    </>
  );
};

export default UserCards;
