import { FunctionComponent, useEffect, useState } from "react";
import { deleteUser, getAllUsers } from "../services/usersServices";
import User from "../interfaces/User";
import { Link } from "react-router-dom";
import { render } from "react-dom";

interface CrmProps {}

const Crm: FunctionComponent<CrmProps> = () => {
  let [users, setUsers] = useState<User[]>([]);
  let [usersChanged, setUsersChanged] = useState<boolean>(false);
  let render = () => {
    setUsersChanged(!usersChanged);
  };

  useEffect(() => {
    getAllUsers()
      .then((res) => {
        console.log(res.data);
        setUsers(res.data);
      })
      .catch((err) => console.log(err));
  }, []);
  let handleDelete = (userId: number) => {
    if (window.confirm("Are you sure?")) {
      deleteUser(userId)
        .then((res) => {
          // successMsg("Product deleted successfully!");
          render();
        })
        .catch((err) => console.log(err));
    }
  };

  return (
    <>
      This is Crm
      {users.length ? (
        <>
          <table className="table">
            <thead>
              <tr>
                <th>User Id</th>
                <th>name</th>
                <th>email</th>
                <th>password</th>
                <th>Edit</th>
                <th>Delete</th>
              </tr>
            </thead>
            <tbody>
              {users.map((user: User) => (
                <tr key={user.userId}>
                  <td>{user.userId}</td>
                  <td>
                    {user.firstName}
                    {user.lastName}
                  </td>
                  <td>{user.email}</td>
                  <td>{user.password}</td>
                  <td>
                    <Link to="/home">
                      <i className="fa-solid fa-pen-to-square" style={{ color: "#28bd32" }}></i>
                    </Link>
                  </td>
                  <td>
                    <Link
                      to=""
                      onClick={() => {
                        handleDelete(user.userId as number);
                      }}
                    >
                      <i className="fa-solid fa-trash"></i>
                    </Link>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </>
      ) : (
        <h3>There are no users yet</h3>
      )}
    </>
  );
};

export default Crm;
