import axios from "axios";
import { getCards } from "./cardsServices";
import Card from "../interfaces/Card";

let api: string = `${process.env.REACT_APP_API}/favorites`;

export function getFavByUserId(userId: number) {
  return axios.get(`${api}?userId=${userId}`);
}
export function deleteFavById(id: number) {
  return axios.delete(`${api}/${id}`);
}
export function addFavCard(userId: number, newCard: Card) {
  return axios.post(api, { userId, ...newCard });
}
