import { useFormik } from "formik";
import { FunctionComponent } from "react";
import { useNavigate, useParams } from "react-router-dom";
import * as yup from "yup";
import { updateUser } from "../services/usersServices";

interface UserProfileProps {}

const UserProfile: FunctionComponent<UserProfileProps> = () => {
  let { id } = useParams();
  let navigate = useNavigate();
  console.log(id);

  let formik = useFormik({
    initialValues: {
      firstName: "",
      middleName: "",
      lastName: "",
      phone: "",
      email: "",
      password: "",
      imageUrl: "",
      imageAlt: "",
      state: "",
      country: "",
      city: "",
      street: "",
      houseNumber: 0,
      zip: "",
      role: "user",
    },
    validationSchema: yup.object({
      firstName: yup.string().min(2, "Please enter a valid name"),
      middleName: yup.string().min(2),
      lastName: yup.string().min(2, "Please enter a valid name"),
      phone: yup.string().min(6, "Please enter a valid Phone number"),
      email: yup.string().email(),
      password: yup
        .string()
        // please Enter your password")
        .matches(
          /^(?=.*[a-z])(?=.*[A-Z])(?=.*[0-9]{4,})(?=.*[!@#\$%\^&\*]*-_)(?=.{8,})/,
