import { FunctionComponent, useEffect, useState } from "react";
import Card from "../interfaces/Card";
import { deleteCard, getCards } from "../services/cardsServices";
import { Link, useNavigate } from "react-router-dom";
import { addToFav } from "../services/favServices";
import { successMsg } from "../services/feedbacksServices";
import { render } from "react-dom";

interface HomeProps {
  userInfo: any;
}

const Home: FunctionComponent<HomeProps> = ({ userInfo }) => {
  let [cards, setCards] = useState<Card[]>([]);
  let [cardsChanged, setCardsChanged] = useState<boolean>(false);

  let navigate = useNavigate();

  let render = () => {
    setCardsChanged(!cardsChanged);
  };
  useEffect(() => {
    getCards()
      .then((res) => setCards(res.data))
      .catch((err) => console.log(err));
  }, [cardsChanged]);

  let handleAddToFav = (card: Card) => {
    let id = userInfo.userId;
    addToFav(id, card)
      .then((res) => {})
      .catch((err) => console.log(err));
  };
  let handleDelete = (card: Card) => {
    deleteCard(card.id as number)
      .then((res) => {
        successMsg("Card deleted successfully!");
        render();
      })
      .catch((err) => console.log(err));
  };
  return (
    <>
      <div className="container " style={{ maxHeight: "87vh" }}>
        {cards.length ? (
          <div className="row row-cols-1 row-cols-md-3 g-4 justify-content-center ">
            {cards.map((card: Card) => (
              <div
                key={card.id}
                className="card mb-3 mx-2"
                style={{ maxWidth: "340px", maxHeight: "500px" }}
              >
                <div className=" g-0 mx-auto align-content-between">
                  <div
                    className="text-center"
                    // style={{ width: "250px", height: "200" }}
                  >
                    <img
                      src={card.imageUrl}
                      className="rounded-start p-1 object-fit-contain "
                      alt={card.imageAlt}
                      width={"200px"}
                      height={"150px"}
                    />
                  </div>
                  <div className="col-md-12">
                    <div className="card-body d-flex flex-column">
                      <h5 className="card-title">{card.title}</h5>
                      <p className="card-text">{card.description}</p>
                      <hr />
                      <p className="card-text">Phone: {card.phone}</p>
                      <p className="card-text">Email: {card.email}</p>
                      <p className="card-text">City: {card.city}</p>
                      <div>
                        {userInfo.role === "admin" && (
                          <>
                            <Link to="/" onClick={() => handleDelete(card)}>
                              <i className="bi bi-trash3-fill"></i>
                            </Link>
                            <Link to="/">
                              <i className="bi bi-pencil-square"></i>
                            </Link>
                          </>
                        )}
                        {userInfo.role && (
                          <Link to="" onClick={() => handleAddToFav(card)}>
                            <i
                              className="bi bi-heart-fill"
                              style={{ color: "#df0c0c", fontSize: "25px" }}
                            />
                          </Link>
                        )}
                        <i
                          className="bi bi-info-square-fill"
                          onClick={() => navigate(`/cards/${card.id}`)}
                        ></i>
                        {/* <button
                          className="btn btn-success align-self-end"
                          onClick={() => navigate(`/cards/${card.id}`)}
                        >
                          more info
                        </button> */}
                      </div>
                    </div>
                  </div>
                </div>
              </div>
            ))}
          </div>
        ) : (
          <h3>No cards to display</h3>
        )}
      </div>
    </>
  );
};

export default Home;
