import { FunctionComponent, useState } from "react";
import { getCardById } from "../services/cardsServices";
import { useParams } from "react-router-dom";
import Card from "../interfaces/Card";

interface CardInfoProps {}

const CardInfo: FunctionComponent<CardInfoProps> = () => {
  let [card, setCard] = useState<Card[]>([]);

  let { id } = useParams();
  getCardById(Number(id));
  return (
    <>
      This is CardInfo
      {card.map((card: Card) => (
        <div
          key={card.id}
          className="card mb-3 mx-2"
          style={{ maxWidth: "340px", maxHeight: "500px" }}
        >
          <div className=" g-0 mx-auto">
            <div
              className="text-center"
              // style={{ width: "250px", height: "200" }}
            >
              <img
                src={card.imageUrl}
                className="rounded-start p-1 object-fit-contain "
                alt={card.imageAlt}
                width={"200px"}
                height={"150px"}
              />
            </div>
            <div className="col-md-12">
              <div className="card-body">
                <h5 className="card-title">{card.title}</h5>
                <p className="card-text">{card.description}</p>
                <hr />
                <p className="card-text">Phone: {card.phone}</p>
                <p className="card-text">Email: {card.email}</p>
                <p className="card-text">City: {card.city}</p>
              </div>
            </div>
          </div>
        </div>
      ))}
    </>
  );
};

export default CardInfo;
