import axios from "axios";
import Card from "../interfaces/Card";
import Favorite from "../interfaces/Favorite";

let api: string = `${process.env.REACT_APP_API}/favorites`;

export function addToFav(userFav: number, cardToAdd: Card) {
  return axios.post(api, { userFav, ...cardToAdd });
}
export function getFav(userFav: number) {
  return axios.get(`${api}?userFav=${userFav}`);
}
export function removeFromFav(id: number, userFav: number) {
  return axios.delete(`${api}/${id}/?userFav=${userFav}`);
}
