import { useFormik } from "formik";
import { FunctionComponent } from "react";
import * as yup from "yup";
import { checkUser } from "../services/usersServices";
import { useNavigate } from "react-router-dom";

interface LoginProps {
  setUserInfo: Function;
}

const Login: FunctionComponent<LoginProps> = ({ setUserInfo }) => {
  let navigate = useNavigate();
  let formik = useFormik({
    initialValues: {
      email: "",
      password: "",
    },
    validationSchema: yup.object({
      email: yup.string().required().email(),
      password: yup.string().required(),
    }),
    onSubmit(values) {
      checkUser(values)
        .then((res) => {
          if (res.data.length) {
            navigate("/home");
            successMsg(`You're logged in as ${values.email}`);
            sessionStorage.setItem(
              "userInfo",
              JSON.stringify({
                email: res.data[0].email,
                isAdmin: res.data[0].isAdmin,
                userId: res.data[0].id,
              })
            );
            setUserInfo(JSON.parse(sessionStorage.getItem("userInfo") as string));
          } else errorMsg("Wrong email or password");
        })
        .catch((err) => console.log(err));
    },
  });
  return (
    <>
      <div className="container-fluid col-md-9 p-5">
        {/***** form *******/}
        <form
          className="text-center p-2 border border-info border-5 rounded-3"
          onSubmit={formik.handleSubmit}
        >
          <h3 className="display-3 bg-info">Register</h3>
          <div className="row">
            <div className="form-floating mb-3 col">
              <input
                type="text"
                className="form-control"
                id="firstName"
                placeholder="Your first name"
                name="firstName"
                value={formik.values.firstName}
                onChange={formik.handleChange}
                onBlur={formik.handleBlur}
              />
              <label htmlFor="firstName">First Name</label>
              {formik.touched.firstName && formik.errors.firstName && (
                <small className="text-danger">{formik.errors.firstName}</small>
              )}
            </div>
            <div className="form-floating mb-3 col">
              <input
                type="text"
                className="form-control"
                id="middleName"
                placeholder="Your middle name"
                name="middleName"
                value={formik.values.middleName}
                onChange={formik.handleChange}
                onBlur={formik.handleBlur}
              />
              <label htmlFor="middleName">Middle Name</label>
              {formik.touched.middleName && formik.errors.middleName && (
                <small className="text-danger">{formik.errors.middleName}</small>
              )}
            </div>
            <div className="form-floating mb-3 col">
