import axios from "axios";
import Card from "../interfaces/Card";
import Favorite from "../interfaces/Favorite";

let api: string = `${process.env.REACT_APP_API}/favorites`;

export function getFav(id: number) {
  return axios.get(`${api}/${id}`);
}

// add to fav / update fav
export async function addToFav(userId: number, cardToAdd: Card) {
  try {
    let res = await getFav(userId);

    if (res.data.length === 0) {
      await createFav(userId);
      res = await getFav(userId);
    }
    res.data.favCards.push({ ...cardToAdd });
    return axios.patch(`${api}/${res.data.id}`, {
      favCards: res.data.favCards,
    });
  } catch (error) {
    console.log(error);
  }
}
// create fav
export function createFav(id: number) {
  return axios.post(api, { id: id, favCards: [] });
}

export async function removeFromFav(userId: number, cardId: number) {
  try {
    let res = await getFav(userId);
    res.data.favCards = res.data.favCards.filter((favCard: Card) => favCard.id !== cardId);
    return axios.patch(`${api}/${res.data.id}`, {
      favCards: res.data.favCards,
    });
  } catch (error) {
    console.log(error);
  }
}

