import axios from "axios";
import Card from "../interfaces/Card";
import Favorite from "../interfaces/Favorite";

let api: string = `${process.env.REACT_APP_API}/favorites`;

export function getFavByCurrentUser(id: number) {
  return axios.get<Favorite>(`${api}?id=${id}`);
}
export function deleteFavById(id: number) {
  return axios.delete(`${api}/${id}`);
}
export async function addFavCard(id: number, cardId: number) {
  const res = await getFavByCurrentUser(id);
  let favorite: Favorite = res.data;
  if (!favorite.id) {
    favorite = {
      id,
      cardIds: [cardId],
    };
  } else if (!favorite.cardIds.includes(cardId)) {
    favorite.cardIds.push(cardId);
  }
  return axios.post(api, favorite);
}
