import axios from "axios";
import Card from "../interfaces/Card";

let api: string = `${process.env.REACT_APP_API}/favorites`;

export function getFavByCurrentUser(userAddedFav: number) {
  return axios.get(`${api}?userAddedFav=${userAddedFav}`);
}
export function deleteFavById(id: number) {
  return axios.delete(`${api}/${id}`);
}
export function addFavCard(userId: number, card: Card) {
  return axios.post(`${api}/favCards`, { userId, card });
}
