import { FunctionComponent, useEffect, useState } from "react";
import Card from "../interfaces/Card";
import { deleteCard, getCards } from "../services/cardsServices";
import { Link } from "react-router-dom";
import FavCards from "./FavCards";

interface HomeProps {
  userInfo: any;
}

const Home: FunctionComponent<HomeProps> = ({ userInfo }) => {
  let [cards, setCards] = useState<Card[]>([]);

  const [favCards, setFavCards] = useState<Card[]>([]);

  const handleFavorite = (card: Card) => {
    if (!favCards.find((favorite) => favorite.id === card.id)) {
      setFavCards([...favCards, card]);
    }
  };
  useEffect(() => {
    getCards()
      .then((res) => setCards(res.data))
      .catch((err) => console.log(err));
  }, []);
  return (
    <>
      <div className="container">
        {cards.length ? (
          <div className="row row-cols-1 row-cols-md-3 g-4">
            {cards.map((card: Card) => (
              <div key={card.id} className="card mb-3 mx-2" style={{ maxWidth: "540px" }}>
                <div className=" g-0">
                  <div className="object-fit-cover" style={{ width: "250px", height: "200" }}>
                    <img
                      src={card.imageUrl}
                      className="img-fluid rounded-start p-1 center"
                      alt={card.imageAlt}
                      // width={"200px"}
                      // height={"150px"}
                    />
                  </div>
                  <div className="col-md-8">
                    <div className="card-body ">
                      <h5 className="card-title">{card.title}</h5>
                      <p className="card-text">{card.description}</p>
                      <hr />
                      <p className="card-text">Phone: {card.phone}</p>
                      <p className="card-text">Email: {card.email}</p>
                      <p className="card-text">City: {card.city}</p>
                      {userInfo.role === "admin" && (
                        <>
                          <Link
                            to="/home"
                            className="btn btn-danger"
                            onClick={() => deleteCard(card.id as number)}
                          >
                            <i className="fa-solid fa-trash"></i>
                          </Link>
                          <Link to="/home" className="btn btn-danger">
                            <i className="fa-regular fa-pen-to-square"></i>
                          </Link>
                        </>
                      )}
                      {(userInfo.role === "user" || userInfo.role === "business") && (
                        <>
                          <Link
                            to="/home"
                            onClick={() => handleFavorite(card)}
                            // className="btn btn-info"
                          >
                            <i className="fa-regular fa-heart" style={{ color: "#e02424" }}></i>
                          </Link>
                        </>
                      )}
                    </div>
                  </div>
                </div>
              </div>
            ))}
          </div>
        ) : (
          <h3>No cards to display</h3>
        )}
      </div>
      <FavCards />
    </>
  );
};

export default Home;
