import { FunctionComponent, useContext, useEffect, useState } from "react";
import Card from "../interfaces/Card";
import { deleteCard, getCards } from "../services/cardsServices";
import { Link, useNavigate } from "react-router-dom";
import { addToFav } from "../services/favServices";
import { SiteTheme } from "../App";
import Login from "./Login";

interface HomeProps {
  userInfo: any;
  setUserInfo: Function;
  darkMode: any;
  setDarkMode: Function;
}

const Home: FunctionComponent<HomeProps> = ({ userInfo, setUserInfo }) => {
  let [cards, setCards] = useState<Card[]>([]);
  let navigate = useNavigate();
  let theme = useContext(SiteTheme);

  useEffect(() => {
    getCards()
      .then((res) => setCards(res.data))
      .catch((err) => console.log(err));
  }, []);
  return (
    <>
      <Login setUserInfo={setUserInfo} userInfo={userInfo} />
      <div className="container " style={{ backgroundColor: theme.background }}>
        {cards.length ? (
          <div className="row row-cols-1 row-cols-md-3 g-4 justify-content-center ">
            {cards.map((card: Card) => (
              <div
                key={card.id}
                className="card mb-3 mx-2"
                style={{ maxWidth: "340px", maxHeight: "500px" }}
              >
                <div className=" g-0 mx-auto">
                  <div className="text-center" style={{ backgroundColor: "#FCF8E8" }}>
                    <img
                      src={card.imageUrl}
                      className="rounded-start p-1 object-fit-contain "
                      alt={card.imageAlt}
                      width={"200px"}
                      height={"150px"}
                    />
                  </div>
                  <div className="col-md-12">
                    <div className="card-body">
                      <h5 className="card-title">{card.title}</h5>
                      <p className="card-text">{card.description}</p>
                      <hr />
                      <p className="card-text">Phone: {card.phone}</p>
                      <p className="card-text">Email: {card.email}</p>
                      <p className="card-text">City: {card.city}</p>
                      {userInfo.role === "admin" && (
                        <>
                          <Link
                            to="/home"
                            className="btn btn-danger"
                            onClick={() => deleteCard(card.id as number)}
                          >
                            <i className="fa-solid fa-trash"></i>
                          </Link>
                          <Link to="/home" className="btn btn-danger">
                            <i className="fa-regular fa-pen-to-square"></i>
                          </Link>
                        </>
                      )}
                      {(userInfo.role === "user" || userInfo.role === "business") && (
                        <>
                          <Link to="" onClick={() => addToFav(userInfo.userId, card)}>
                            <i className="fa-regular fa-heart fa-xl"></i>
                          </Link>
                        </>
                      )}
                      <Link
                        to=""
                        className="btn "
                        style={{ backgroundColor: "#78C1F3", marginLeft: "5px" }}
                        onClick={() => navigate(`/cards/${card.id}`)}
                      >
                        more info
                      </Link>
                    </div>
                  </div>
                </div>
              </div>
            ))}
          </div>
        ) : (
          <h3>No cards to display</h3>
        )}
      </div>
    </>
  );
};

export default Home;
