import { FunctionComponent, useEffect, useState } from "react";
import { useFormik } from "formik";
import { useNavigate, useParams } from "react-router-dom";
import { getCardById, updateCard } from "../services/cardsServices";
import * as yup from "yup";
import Card from "../interfaces/Card";

interface UpdateCardProps {}

const UpdateCard: FunctionComponent<UpdateCardProps> = () => {
  let { id } = useParams();
  let navigate = useNavigate();

  let [card, setCard] = useState<Card>({
    // id: 0,
    title: "",
    subTitle: "",
    description: "",
    phone: "",
    email: "",
    web: "",
    imageUrl: "",
    imageAlt: "",
    state: "",
    country: "",
    city: "",
    street: "",
    houseNumber: 0,
    zip: "",
  });

  useEffect(() => {
    getCardById(Number(id))
      .then((res) => {
        setCard(res.data);
      })
      .catch((error) => console.log(error));
  }, []);

  let formik = useFormik({
    initialValues: {
      // id: card.id,
      userId: card.userId,
      title: card.title,
      subTitle: card.subTitle,
      description: card.description,
      phone: card.phone,
      email: card.email,
      web: card.web,
      imageUrl: card.imageUrl,
      imageAlt: card.imageAlt,
      state: card.state,
      country: card.country,
      city: card.city,
      street: card.street,
      houseNumber: card.houseNumber,
      zip: card.zip,
    },
    enableReinitialize: true,

    validationSchema: yup.object({
      title: yup.string().required().min(2, "Please enter a valid title"),
      subTitle: yup.string().required().min(2),
      description: yup.string().min(2, "Please enter a valid name"),
      phone: yup.string().min(6, "Please enter a valid Phone number"),
      email: yup.string().email(),
      web: yup.string().url(),
      imageUrl: yup.string().url(),
      imageAlt: yup.string().min(2),
      state: yup.string(),
      country: yup.string(),
      city: yup.string(),
      street: yup.string(),
      houseNumber: yup.number(),
