import axios from "axios";
import Card from "../interfaces/Card";

let api: string = `${process.env.REACT_APP_API}/cards`;

export function getCards() {
  return axios.get(api);
}

export function createCard(userId: number, newCard: Card) {
  return axios.post(api, { userId, ...newCard });
}

export function deleteCard(id: number) {
  return axios.delete(`${api}/${id}`);
}
export function getCardsByUserId(userId: number) {
  return axios.get(`${api}?userId=${userId}`);
}
export function getCardsById(id: number) {
  return axios.get(`${api}/${id}`);
}

export function updateCard(updatedCard: Card, id: number) {
  return axios.put(`${api}/${id}`, updatedCard);
}
