import { FunctionComponent, useEffect } from "react";
import { useFormik } from "formik";
import { useNavigate, useParams } from "react-router-dom";
import { getCardsByUserId, updateCard } from "../services/cardsServices";
import * as yup from "yup";
import { error } from "console";

interface UpdateCardProps {}

const UpdateCard: FunctionComponent<UpdateCardProps> = () => {
  let { id } = useParams();
  let navigate = useNavigate();
  useEffect(() => {
    getCardsByUserId(Number(id))
      .then((res) => setCard(res.data))
      .catch((error) => console.log(error));
  }, []);
  let formik = useFormik({
    initialValues: {
      title: "",
      subTitle: "",
      description: "",
      phone: "",
      email: "",
      web: "",
      imageUrl: "",
      imageAlt: "",
      state: "",
      country: "",
      city: "",
      street: "",
      houseNumber: 0,
      zip: "",
    },

    validationSchema: yup.object({
      title: yup.string().required().min(2, "Please enter a valid title"),
      subTitle: yup.string().required().min(2),
      description: yup.string().min(2, "Please enter a valid name"),
      phone: yup.string().min(6, "Please enter a valid Phone number"),
      email: yup.string().email(),
      web: yup.string().url(),
      imageUrl: yup.string().url(),
      imageAlt: yup.string().min(2),
      state: yup.string(),
      country: yup.string(),
      city: yup.string(),
      street: yup.string(),
      houseNumber: yup.number(),
      zip: yup.string(),
    }),
    onSubmit: (values) => {
      console.log(values);
      let userId: number = JSON.parse(sessionStorage.getItem("userInfo") as string).userId;
      console.log(userId);
      updateCard(values, userId)
        .then((res) => navigate(-1))
        .catch((error) => console.log(error));
    },
  });

  return (
    <>
      <div className="container-fluid col-md-9 p-5">
        {/***** form *******/}
        <form
          className="text-center p-2 border border-info border-5 rounded-3"
          onSubmit={formik.handleSubmit}
        >
          <h3 className="display-3 bg-info">Create card</h3>
          <div className="row">
            <div className="form-floating mb-3 col">
              <input
                type="text"
                className="form-control"
                id="firstName"
                placeholder="Title"
                name="title"
                value={formik.values.title}
                onChange={formik.handleChange}
                onBlur={formik.handleBlur}
              />
              <label htmlFor="firstName">Title</label>
              {formik.touched.title && formik.errors.title && (
                <small className="text-danger">{formik.errors.title}</small>
              )}
            </div>
            <div className="form-floating mb-3 col">
              <input
