import { createContext, useState } from "react";
import "./App.css";
import Home from "./components/Home";
import Navbar from "./components/Navbar";
import Register from "./components/Register";
import { BrowserRouter as Router, Routes, Route } from "react-router-dom";
import Login from "./components/Login";
import CreateCard from "./components/CreateCard";
import UserCards from "./components/UserCards";
import FavCards from "./components/FavCards";
import Footer from "./components/Footer";
import UpdateCard from "./components/UpdateCard";
import CardInfo from "./components/CardInfo";
import UserProfile from "./components/UserProfile";
import About from "./components/About";
import Crm from "./components/Crm";

let themes = {
  light: {
    color: "black",
    background: "#D4E2D4",
  },
  dark: {
    color: "white",
    background: "#3F2E3E",
  },
};
export let SiteTheme = createContext(themes.light);

function App() {
  let [darkMode, setDarkMode] = useState<boolean>(false);
  let [userInfo, setUserInfo] = useState(
    JSON.parse(sessionStorage.getItem("userInfo") as string) == null
      ? { email: false, isAdmin: false }
      : JSON.parse(sessionStorage.getItem("userInfo") as string)
  );

  return (
    <div className="App">
      <Router>
        <SiteTheme.Provider value={darkMode ? themes.dark : themes.light}>
          <Navbar
            setUserInfo={setUserInfo}
            userInfo={userInfo}
            darkMode={darkMode}
            setDarkMode={setDarkMode}
          />
          <Routes>
            <Route
              path="/home"
              element={
                <Home
                  userInfo={userInfo}
                  setUserInfo={setUserInfo}
                  darkMode={darkMode}
                  setDarkMode={setDarkMode}
                />
              }
            />
            <Route path="/aboutSniffle" element={<About />} />
            <Route path="/crm" element={<Crm />} />
            <Route path="/favorites" element={<FavCards userInfo={userInfo} />} />
            <Route
              path="/login"
              element={<Login setUserInfo={setUserInfo} userInfo={userInfo} />}
            />
            <Route path="/register" element={<Register setUserInfo={setUserInfo} />} />
            <Route path="/cards/new" element={<CreateCard userInfo={userInfo} />} />
            <Route path="/user-cards" element={<UserCards userInfo={userInfo} />} />
            <Route path="/cards/update/:id" element={<UpdateCard />} />
            <Route path="/cards/:id" element={<CardInfo />} />
            <Route
              path="/user-profile/:userId"
              element={<UserProfile setUserInfo={setUserInfo} userInfo={userInfo} />}
            />
          </Routes>
        </SiteTheme.Provider>
      </Router>
      <Footer />
    </div>
  );
}

export default App;
