import { FunctionComponent, useEffect, useState } from "react";
import Card from "../interfaces/Card";
import { Link } from "react-router-dom";
import { deleteFavById, getFavByCurrentUser } from "../services/favServices";
import { getCardsById } from "../services/cardsServices";

interface FavCardsProps {
  userInfo: any;

  // favCards: Card[];
  // setFavCards: Function;
  // handleFavorite: (card: Card) => void;
  // isCardFavorite: (card: Card) => boolean;
}

const FavCards: FunctionComponent<FavCardsProps> = ({
  userInfo,

  // favCards,
  // setFavCards,
  // handleFavorite,
  // isCardFavorite,
}) => {
  let [favCards, setFavCards] = useState<Card[]>([]);
  let removeFromFav = (card: Card) => {
    setFavCards(favCards.filter((favCard) => favCard.id !== card.id));
    deleteFavById(card.id as number);
  };

  useEffect(() => {
    getFavByCurrentUser(userInfo.userId)
      .then((res) => {
        getCardsById(res.data.cardIds).then((res1) => setFavCards(res1.data));
      })
      .catch((err) => console.log(err));
  }, []);
  return (
    <>
      <div className="container">
        {favCards.length ? (
          <div className="row row-cols-1 row-cols-md-3 g-4">
            {favCards.map((card: Card) => (
              <div key={card.id} className="card mb-3 mx-2" style={{ maxWidth: "540px" }}>
                <div className=" g-0">
                  <div className="object-fit-cover" style={{ width: "250px", height: "200" }}>
                    <img
                      src={card.imageUrl}
                      className="img-fluid rounded-start p-1 center"
                      alt={card.imageAlt}
                      // width={"200px"}
                      // height={"150px"}
                    />
                  </div>
                  <div className="col-md-8">
                    <div className="card-body ">
                      <h5 className="card-title">{card.title}</h5>
                      <p className="card-text">{card.description}</p>
                      <hr />
                      <p className="card-text">Phone: {card.phone}</p>
                      <p className="card-text">Email: {card.email}</p>
                      <p className="card-text">City: {card.city}</p>

                      <button onClick={() => removeFromFav(card)}>
                        <i className="fa-regular fa-heart fa-xl"></i>
                      </button>
                    </div>
                  </div>
                </div>
              </div>
            ))}
          </div>
        ) : (
          <h3>No cards to display</h3>
        )}
      </div>
    </>
  );
};

export default FavCards;
