import { render } from "@testing-library/react";
import { FunctionComponent } from "react";
import { Link, NavLink, useNavigate } from "react-router-dom";

interface NavbarProps {
  setUserInfo: Function;
  userInfo: any;
}

const Navbar: FunctionComponent<NavbarProps> = ({ setUserInfo, userInfo }) => {
  let navigate = useNavigate();
  let render = () => setUserInfo(!userInfo);

  let logout = () => {
    sessionStorage.removeItem("userInfo");
    // setUserInfo({ email: false, isAdmin: false });
    navigate("/home");
    render();
  };
  return (
    <>
      <nav className="navbar navbar-expand-lg navbar-light bg-light">
        <div className="container-fluid">
          <button
            className="navbar-toggler"
            type="button"
            data-bs-toggle="collapse"
            data-bs-target="#navbarTogglerDemo03"
            aria-controls="navbarTogglerDemo03"
            aria-expanded="false"
            aria-label="Toggle navigation"
          >
            <span className="navbar-toggler-icon"></span>
          </button>
          <NavLink to={"/home"} className="navbar-brand">
            <img src="logo.png" alt="" width="60" height="44" />
          </NavLink>
          <div className="collapse navbar-collapse" id="navbarTogglerDemo03">
            <ul className="navbar-nav me-auto mb-2 mb-lg-0">
              <li className="nav-item">
                <a className="nav-link active" aria-current="page" href="#">
                  About Sniffle
                </a>
              </li>

              {/**** business user ****/}
              {userInfo.role === "business" && (
                <li className="nav-item">
                  <a className="nav-link active" aria-current="page" href="#">
                    My cards
                  </a>
                </li>
              )}
            </ul>
            <form className="d-flex">
              <input
                className="form-control me-2"
                type="search"
                placeholder="Search"
                aria-label="Search"
              />
              <button className="btn btn-outline-success" type="submit">
                Search
              </button>
              <ul className="navbar-nav me-auto mb-2 mb-lg-0">
                <li className="nav-item">
                  <NavLink className="nav-link" to={"/login"}>
                    Login
                  </NavLink>
                </li>
                <li className="nav-item">
                  <NavLink className="nav-link" to={"/register"}>
                    Register
                  </NavLink>
                </li>
                <li className="nav-item">
                  <NavLink className="nav-link" to={"/register"} onClick={logout}>
                    Logout
                  </NavLink>
                </li>
                <li className="nav-item">
                  <NavLink className="nav-link" to={"/userProfile"}>
                    <i className="fa-solid fa-user"></i>
                  </NavLink>
                </li>
              </ul>
            </form>
          </div>
        </div>
      </nav>
    </>
  );
};

export default Navbar;
