import { FunctionComponent } from "react";
import { useFormik } from "formik";
import * as yup from "yup";

interface RegisterProps {}

const Register: FunctionComponent<RegisterProps> = () => {
  let formik = useFormik({
    initialValues: { firstName: "", middleName: "", lastName: "" },
    validationSchema: yup.object({
      firstName: yup.string().required().min(2, "Please enter a valid name"),
      middleName: yup.string().min(2),
      lastName: yup.string().required().min(2),
    }),
    onSubmit() {},
  });
  return (
    <>
      <h3 className="display-3">REGISTER</h3>

      <div className="form-floating mb-3">
        <input
          type="text"
          className="form-control"
          id="firstName"
          placeholder="Your first name"
          name="firstName"
          value={formik.values.firstName}
          onChange={formik.handleChange}
          onBlur={formik.handleBlur}
        />
        <label htmlFor="firstName">First Name</label>
        {formik.touched.firstName && formik.errors.firstName && (
          <small className="text-danger">{formik.errors.firstName}</small>
        )}
      </div>
      <div className="form-floating mb-3">
        <input
          type="text"
          className="form-control"
          id="middleName"
          placeholder="Your middle name"
          name="middleName"
          value={formik.values.middleName}
          onChange={formik.handleChange}
          onBlur={formik.handleBlur}
        />
        <label htmlFor="middleName">Middle Name</label>
        {formik.touched.middleName && formik.errors.middleName && (
          <small className="text-danger">{formik.errors.middleName}</small>
        )}
      </div>
      <div className="form-floating mb-3">
        <input
          type="text"
          className="form-control"
          id="lastName"
          placeholder="Your lastName name"
          name="lastName"
          value={formik.values.lastName}
          onChange={formik.handleChange}
          onBlur={formik.handleBlur}
        />
        <label htmlFor="lastName">lastName</label>
        {formik.touched.middleName && formik.errors.lastName && (
          <small className="text-danger">{formik.errors.lastName}</small>
        )}
      </div>
      <div className="form-floating">
        <input
          type="password"
          className="form-control"
          id="floatingPassword"
          placeholder="Password"
        />
        <label htmlFor="floatingPassword">Password</label>
      </div>
    </>
  );
};

export default Register;
